function convertToRoman(num) {
    var numbers = [1,4,5,9,10,40,50,90,100,400,500,900,1000]; 
	var roman = ["I","IV","V","IX","X","XL","L","XC","C","CD","D","CM","M"]; 
	var i=12; 
    var string="";
    while(num > 0) {
        while(num >= numbers[i]) {
            string += roman[i];
            num -= numbers[i];
        }
		i--;
    }
    return string; 
}

convertToRoman(36);
